mapper.update:
UPDATE classes a
   SET a.id = a.id

       ,a.grade_uuid = #{paramMap.gradeUuid, jdbcType=VARCHAR}


       ,a.uuid = #{paramMap.uuid, jdbcType=VARCHAR}


       ,a.name = #{paramMap.name, jdbcType=VARCHAR}


       ,a.status = #{paramMap.status, jdbcType=INTEGER}


       ,a.created_time = #{paramMap.createdTime, jdbcType=VARCHAR}


       ,a.modified_time = #{paramMap.modifiedTime, jdbcType=VARCHAR}


       ,a.hikclassid = #{paramMap.hikclassid, jdbcType=VARCHAR}


       ,a.yhclassname = #{paramMap.yhclassname, jdbcType=VARCHAR}

 WHERE a.id = #{id, jdbcType=BIGINT}

mapper.select:
SELECT a.id id, a.grade_uuid gradeUuid, a.uuid uuid, a.name name, a.status status, DATE_FORMAT(a.created_time, '%Y-%m-%d %T') createdTime, DATE_FORMAT(a.modified_time, '%Y-%m-%d %T') modifiedTime, a.hikclassid hikclassid, a.yhclassname yhclassname FROM classes a WHERE 1=1
AND a.id = #{id, jdbcType=BIGINT}
AND a.grade_uuid = #{gradeUuid, jdbcType=VARCHAR}
AND a.uuid = #{uuid, jdbcType=VARCHAR}
AND a.status = #{status, jdbcType=INTEGER}

