mapper.update:
UPDATE grades a
   SET a.id = a.id

       ,a.school_uuid = #{paramMap.schoolUuid, jdbcType=VARCHAR}


       ,a.uuid = #{paramMap.uuid, jdbcType=VARCHAR}


       ,a.name = #{paramMap.name, jdbcType=VARCHAR}


       ,a.status = #{paramMap.status, jdbcType=INTEGER}


       ,a.created_time = #{paramMap.createdTime, jdbcType=VARCHAR}


       ,a.modified_time = #{paramMap.modifiedTime, jdbcType=VARCHAR}


       ,a.type_uuid = #{paramMap.typeUuid, jdbcType=VARCHAR}


       ,a.grade_session = #{paramMap.gradeSession, jdbcType=INTEGER}


       ,a.grade_level = #{paramMap.gradeLevel, jdbcType=VARCHAR}


       ,a.type = #{paramMap.type, jdbcType=CHAR}

 WHERE a.id = #{id, jdbcType=BIGINT}

mapper.select:
SELECT a.id id, a.school_uuid schoolUuid, a.uuid uuid, a.name name, a.status status, DATE_FORMAT(a.created_time, '%Y-%m-%d %T') createdTime, DATE_FORMAT(a.modified_time, '%Y-%m-%d %T') modifiedTime, a.type_uuid typeUuid, a.grade_session gradeSession, a.grade_level gradeLevel, a.type type FROM grades a WHERE 1=1
AND a.id = #{id, jdbcType=BIGINT}
AND a.school_uuid = #{schoolUuid, jdbcType=VARCHAR}
AND a.uuid = #{uuid, jdbcType=VARCHAR}
AND a.status = #{status, jdbcType=INTEGER}
AND a.type_uuid = #{typeUuid, jdbcType=VARCHAR}

