mapper.update:
UPDATE t_schools_function a
   SET a.id = a.id

       ,a.schooluuid = #{paramMap.schooluuid, jdbcType=VARCHAR}


       ,a.hik_cloud = #{paramMap.hikCloud, jdbcType=CHAR}


       ,a.hkschooluuid = #{paramMap.hkschooluuid, jdbcType=VARCHAR}


       ,a.yun_hen = #{paramMap.yunHen, jdbcType=CHAR}


       ,a.yhschoolid = #{paramMap.yhschoolid, jdbcType=VARCHAR}


       ,a.yun_hen_phone = #{paramMap.yunHenPhone, jdbcType=VARCHAR}


       ,a.yi_xue_yun = #{paramMap.yiXueYun, jdbcType=CHAR}


       ,a.yxyschoolid = #{paramMap.yxyschoolid, jdbcType=VARCHAR}


       ,a.create_date = #{paramMap.createDate, jdbcType=VARCHAR}


       ,a.update_date = #{paramMap.updateDate, jdbcType=VARCHAR}


       ,a.create_by = #{paramMap.createBy, jdbcType=VARCHAR}


       ,a.update_by = #{paramMap.updateBy, jdbcType=VARCHAR}


       ,a.del_flag = #{paramMap.delFlag, jdbcType=CHAR}


       ,a.remarks = #{paramMap.remarks, jdbcType=VARCHAR}


       ,a.abc_school = #{paramMap.abcSchool, jdbcType=CHAR}


       ,a.epad_type = #{paramMap.epadType, jdbcType=INTEGER}


       ,a.seewo_schooluid = #{paramMap.seewoSchooluid, jdbcType=VARCHAR}

 WHERE a.id = #{id, jdbcType=BIGINT}

mapper.select:
SELECT a.id id, a.schooluuid schooluuid, a.hik_cloud hikCloud, a.hkschooluuid hkschooluuid, a.yun_hen yunHen, a.yhschoolid yhschoolid, a.yun_hen_phone yunHenPhone, a.yi_xue_yun yiXueYun, a.yxyschoolid yxyschoolid, DATE_FORMAT(a.create_date, '%Y-%m-%d %T') createDate, DATE_FORMAT(a.update_date, '%Y-%m-%d %T') updateDate, a.create_by createBy, a.update_by updateBy, a.del_flag delFlag, a.remarks remarks, a.abc_school abcSchool, a.epad_type epadType, a.seewo_schooluid seewoSchooluid FROM t_schools_function a WHERE 1=1
AND a.schooluuid = #{schooluuid, jdbcType=VARCHAR}
AND a.yhschoolid = #{yhschoolid, jdbcType=VARCHAR}
AND a.yxyschoolid = #{yxyschoolid, jdbcType=VARCHAR}
AND a.del_flag = #{delFlag, jdbcType=CHAR}
AND a.epad_type = #{epadType, jdbcType=INTEGER}
AND a.seewo_schooluid = #{seewoSchooluid, jdbcType=VARCHAR}

