mapper.update:
UPDATE schools a
   SET a.id = a.id

       ,a.area_uuid = #{paramMap.areaUuid, jdbcType=VARCHAR}


       ,a.uuid = #{paramMap.uuid, jdbcType=VARCHAR}


       ,a.name = #{paramMap.name, jdbcType=VARCHAR}


       ,a.status = #{paramMap.status, jdbcType=INTEGER}


       ,a.created_time = #{paramMap.createdTime, jdbcType=VARCHAR}


       ,a.modified_time = #{paramMap.modifiedTime, jdbcType=VARCHAR}


       ,a.type = #{paramMap.type, jdbcType=INTEGER}


       ,a.car_flag = #{paramMap.carFlag, jdbcType=TINYINT}


       ,a.yunke_flag = #{paramMap.yunkeFlag, jdbcType=CHAR}


       ,a.yunke_code = #{paramMap.yunkeCode, jdbcType=VARCHAR}

 WHERE a.id = #{id, jdbcType=BIGINT}

mapper.select:
SELECT a.id	id,
       a.area_uuid	areaUuid,
       a.uuid	uuid,
       a.name	name,
       a.status	status,
       DATE_FORMAT(a.created_time, '%Y-%m-%d %T')	createdTime,
       DATE_FORMAT(a.modified_time, '%Y-%m-%d %T')	modifiedTime,
       a.type	type,
       a.car_flag	carFlag,
       a.yunke_flag	yunkeFlag,
       a.yunke_code	yunkeCode
  FROM schools a
 WHERE 1=1

   AND a.area_uuid = #{areaUuid, jdbcType=VARCHAR}


   AND a.uuid = #{uuid, jdbcType=VARCHAR}


   AND a.status = #{status, jdbcType=INTEGER}


   AND a.type = #{type, jdbcType=INTEGER}


   AND a.car_flag = #{carFlag, jdbcType=TINYINT}


   AND a.yunke_flag = #{yunkeFlag, jdbcType=CHAR}


   AND a.yunke_code = #{yunkeCode, jdbcType=VARCHAR}

