mapper.update:
UPDATE sys_office a
   SET a.id = a.id

       ,a.parent_id = #{paramMap.parentId, jdbcType=VARCHAR}


       ,a.parent_ids = #{paramMap.parentIds, jdbcType=VARCHAR}


       ,a.name = #{paramMap.name, jdbcType=VARCHAR}


       ,a.sort = #{paramMap.sort, jdbcType=DECIMAL}


       ,a.area_id = #{paramMap.areaId, jdbcType=VARCHAR}


       ,a.code = #{paramMap.code, jdbcType=VARCHAR}


       ,a.type = #{paramMap.type, jdbcType=CHAR}


       ,a.grade = #{paramMap.grade, jdbcType=CHAR}


       ,a.address = #{paramMap.address, jdbcType=VARCHAR}


       ,a.zip_code = #{paramMap.zipCode, jdbcType=VARCHAR}


       ,a.master = #{paramMap.master, jdbcType=VARCHAR}


       ,a.phone = #{paramMap.phone, jdbcType=VARCHAR}


       ,a.fax = #{paramMap.fax, jdbcType=VARCHAR}


       ,a.email = #{paramMap.email, jdbcType=VARCHAR}


       ,a.useable = #{paramMap.useable, jdbcType=VARCHAR}


       ,a.primary_person = #{paramMap.primaryPerson, jdbcType=VARCHAR}


       ,a.deputy_person = #{paramMap.deputyPerson, jdbcType=VARCHAR}


       ,a.create_by = #{paramMap.createBy, jdbcType=VARCHAR}


       ,a.create_date = #{paramMap.createDate, jdbcType=VARCHAR}


       ,a.update_by = #{paramMap.updateBy, jdbcType=VARCHAR}


       ,a.update_date = #{paramMap.updateDate, jdbcType=VARCHAR}


       ,a.remarks = #{paramMap.remarks, jdbcType=VARCHAR}


       ,a.del_flag = #{paramMap.delFlag, jdbcType=CHAR}


       ,a.grade_level = #{paramMap.gradeLevel, jdbcType=VARCHAR}

 WHERE a.id = #{id, jdbcType=VARCHAR}

