SELECT a.id id, a.parent_id parentId, a.parent_ids parentIds, a.name name, a.sort sort, a.area_id areaId, a.code code, a.type type, a.grade grade, a.address address, a.zip_code zipCode, a.master master, a.phone phone, a.fax fax, a.email email, a.useable useable, a.primary_person primaryPerson, a.deputy_person deputyPerson, a.create_by createBy, DATE_FORMAT(a.create_date, '%Y-%m-%d %T') createDate, a.update_by updateBy, DATE_FORMAT(a.update_date, '%Y-%m-%d %T') updateDate, a.remarks remarks, a.del_flag delFlag, a.grade_level gradeLevel FROM sys_office a WHERE 1=1
AND a.id = #{id, jdbcType=VARCHAR}
AND a.parent_id = #{parentId, jdbcType=VARCHAR}
AND a.area_id = #{areaId, jdbcType=VARCHAR}
AND a.type = #{type, jdbcType=CHAR}
AND a.grade = #{grade, jdbcType=CHAR}
AND a.del_flag = #{delFlag, jdbcType=CHAR}

