mapper.update:
UPDATE sys_user a
   SET a.id = a.id

       ,a.office_id = #{paramMap.officeId, jdbcType=VARCHAR}


       ,a.login_name = #{paramMap.loginName, jdbcType=VARCHAR}


       ,a.password = #{paramMap.password, jdbcType=VARCHAR}


       ,a.no = #{paramMap.no, jdbcType=VARCHAR}


       ,a.name = #{paramMap.name, jdbcType=VARCHAR}


       ,a.email = #{paramMap.email, jdbcType=VARCHAR}


       ,a.phone = #{paramMap.phone, jdbcType=VARCHAR}


       ,a.mobile = #{paramMap.mobile, jdbcType=VARCHAR}


       ,a.user_type = #{paramMap.userType, jdbcType=CHAR}


       ,a.photo = #{paramMap.photo, jdbcType=VARCHAR}


       ,a.login_ip = #{paramMap.loginIp, jdbcType=VARCHAR}


       ,a.login_date = #{paramMap.loginDate, jdbcType=VARCHAR}


       ,a.login_flag = #{paramMap.loginFlag, jdbcType=VARCHAR}


       ,a.create_by = #{paramMap.createBy, jdbcType=VARCHAR}


       ,a.create_date = #{paramMap.createDate, jdbcType=VARCHAR}


       ,a.update_by = #{paramMap.updateBy, jdbcType=VARCHAR}


       ,a.update_date = #{paramMap.updateDate, jdbcType=VARCHAR}


       ,a.remarks = #{paramMap.remarks, jdbcType=VARCHAR}


       ,a.del_flag = #{paramMap.delFlag, jdbcType=CHAR}

 WHERE a.id = #{id, jdbcType=VARCHAR}

