AND a.id = #{id, jdbcType=VARCHAR}
AND a.office_id = #{officeId, jdbcType=VARCHAR}
AND a.user_type = #{userType, jdbcType=CHAR}
AND a.del_flag = #{delFlag, jdbcType=CHAR}

