mapper.update:
UPDATE users a
   SET a.id = a.id

       ,a.uuid = #{paramMap.uuid, jdbcType=VARCHAR}


       ,a.phone = #{paramMap.phone, jdbcType=VARCHAR}


       ,a.password = #{paramMap.password, jdbcType=VARCHAR}


       ,a.firstname = #{paramMap.firstname, jdbcType=VARCHAR}


       ,a.lastname = #{paramMap.lastname, jdbcType=VARCHAR}


       ,a.nickname = #{paramMap.nickname, jdbcType=VARCHAR}


       ,a.type = #{paramMap.type, jdbcType=INTEGER}


       ,a.status = #{paramMap.status, jdbcType=INTEGER}


       ,a.created_time = #{paramMap.createdTime, jdbcType=VARCHAR}


       ,a.modified_time = #{paramMap.modifiedTime, jdbcType=VARCHAR}


       ,a.pic_url = #{paramMap.picUrl, jdbcType=VARCHAR}


       ,a.wonderful_time = #{paramMap.wonderfulTime, jdbcType=VARCHAR}


       ,a.easemob_uuid = #{paramMap.easemobUuid, jdbcType=VARCHAR}


       ,a.phone_type = #{paramMap.phoneType, jdbcType=TINYINT}


       ,a.access_token = #{paramMap.accessToken, jdbcType=VARCHAR}


       ,a.login_time = #{paramMap.loginTime, jdbcType=VARCHAR}


       ,a.user_code = #{paramMap.userCode, jdbcType=VARCHAR}


       ,a.identity_num = #{paramMap.identityNum, jdbcType=VARCHAR}


       ,a.current_type = #{paramMap.currentType, jdbcType=CHAR}


       ,a.current_uuid = #{paramMap.currentUuid, jdbcType=VARCHAR}


       ,a.yunhteacherid = #{paramMap.yunhteacherid, jdbcType=VARCHAR}


       ,a.yunhteachercode = #{paramMap.yunhteachercode, jdbcType=VARCHAR}

 WHERE a.id = #{id, jdbcType=BIGINT}

mapper.select:
SELECT a.id id, a.uuid uuid, a.phone phone, a.password password, a.firstname firstname, a.lastname lastname, a.nickname nickname, a.type type, a.status status, DATE_FORMAT(a.created_time, '%Y-%m-%d %T') createdTime, DATE_FORMAT(a.modified_time, '%Y-%m-%d %T') modifiedTime, a.pic_url picUrl, DATE_FORMAT(a.wonderful_time, '%Y-%m-%d %T') wonderfulTime, a.easemob_uuid easemobUuid, a.phone_type phoneType, a.access_token accessToken, DATE_FORMAT(a.login_time, '%Y-%m-%d %T') loginTime, a.user_code userCode, a.identity_num identityNum, a.current_type currentType, a.current_uuid currentUuid, a.yunhteacherid yunhteacherid, a.yunhteachercode yunhteachercode FROM users a WHERE 1=1
AND a.uuid = #{uuid, jdbcType=VARCHAR}
AND a.phone = #{phone, jdbcType=VARCHAR}
AND a.type = #{type, jdbcType=INTEGER}
AND a.status = #{status, jdbcType=INTEGER}
AND a.phone_type = #{phoneType, jdbcType=TINYINT}
AND a.user_code = #{userCode, jdbcType=VARCHAR}
AND a.current_type = #{currentType, jdbcType=CHAR}
AND a.current_uuid = #{currentUuid, jdbcType=VARCHAR}

